const debouncedSearch = debounce((query) => {

// Perform search here

}, 300); // Debounce for 300 milliseconds

function SearchInput() {

const handleSearch = (e) => {

debouncedSearch(e.target.value);

};

return <input type="text" onChange={handleSearch} />;

}

