// Example 1: Model (TodoList.js)

class TodoList {

constructor() {

this.tasks = [];

}

addTask(task) {

this.tasks.push(task);

}

deleteTask(task) {

this.tasks = this.tasks.filter((t) => t !== task);

}

getAllTasks() {

return this.tasks;

}

}

module.exports = TodoList;

```

```jsjavascript

// Example 2: View (TodoListView.js)

class TodoListView {

constructor() {

this.todoListElement = document.getElementById('todo-list');

}

renderTasks(tasks) {

this.todoListElement.innerHTML = '';

tasks.forEach((task) => {

const listItem = document.createElement('li');

listItem.textContent = task;

this.todoListElement.appendChild(listItem);

});

}

}

module.exports = TodoListView;

```

```jsjavascript

// Example 3: Controller (TodoListController.js)

class TodoListController {

constructor(model, view) {

this.model = model;

this.view = view;

this.view.renderTasks(this.model.getAllTasks());

// Event listener to handle user input

document.getElementById('add-task').addEventListener('click', () => {

const taskInput = document.getElementById('task-input');

const newTask = taskInput.value;

if (newTask) {

this.model.addTask(newTask);

this.view.renderTasks(this.model.getAllTasks());

taskInput.value = '';

}

});

}

}

module.exports = TodoListController;

