const ensureAuthenticated = (req, res, next) => {

if (req.isAuthenticated()) {

return next();

}

res.redirect('/login');

};

// Example usage: Protect the create route

router.post('/create', ensureAuthenticated, (req, res) => {

// Logic to create a new blog post

});

