```jsjavascript

const express = require('express');

const router = express.Router();

const { Client } = require('elasticsearch');

// Create Elasticsearch client

const client = new Client({ node: 'http://localhost:9200' });

// Search route

router.get('/', (req, res) => {

const { query } = req.query;

// Perform search query

client

.search({

index: 'blog_posts',

body: {

query: {

match: {

content: query,

},

},

},

})

.then((response) => {

const hits = response.body.hits.hits.map((hit) => hit._source);

res.status(200).json(hits);

})

.catch((error) => {

res.status(500).json({ error: 'An error occurred while performing the search' });

});

});

module.exports = router;

```

```jsjavascript

const express = require('express');

const router = express.Router();

const Post = require('../models/post');

const { Client } = require('elasticsearch');

// Create Elasticsearch client

const client

= new Client({ node: 'http://localhost:9200' });

// Create post route

router.post('/create', (req, res) => {

const { title, content } = req.body;

// Validate input fields

if (!title || !content) {

return res.status(400).json({ error: 'Please provide a title and content for the post' });

}

// Create a new post

const newPost = new Post({ title, content });

// Save the post to the database

newPost.save()

.then(() => {

// Index the new post in Elasticsearch

client.index({

index: 'blog_posts',

body: {

title,

content,

},

});

res.status(201).json({ message: 'Post created successfully' });

})

.catch((error) => {

res.status(500).json({ error: 'An error occurred while creating the post' });

});

});

module.exports = router;

```
